import React, { Component } from 'react';
import ReactDom from 'react-dom';
import './index.css'
import './constants'
import constants from './constants';
import QuestionBox from './components/questionbox'

class QuizApp extends Component{

    state = {
        listOfQuestions : [],
        score : 0,
        responses : 0
    };

    getQuestions = () => {
        constants().then(questions => {
            this.setState({
                listOfQuestions : questions
            });
        });
    }

    componentDidMount(){
        this.getQuestions();
    }

    computeAnswer = (answer, correctAnswer) => {
        if(answer === correctAnswer){
            this.setState({
                score:this.setState.score+1
            })
        }
        this.setState({
            responses:this.state.responses
        })
    }

    render(){
        return (
            <div className='app-container'>
                <div className="title"> Quiz </div>
                {
                    this.state.listOfQuestions.length > 0 && this.state.listOfQuestions.map(
                        ({question, answers, correct, questionId}) => (
                            <QuestionBox question={question} options={answers} key={questionId} selected={answer => this.computeAnswer(answer,correct)} />
                        )

                    )
                }
            </div>
        );
    }
}

ReactDom.render(<QuizApp />,document.getElementById('root'));
